DROP TABLE IF EXISTS `trainingset`;
CREATE TABLE `trainingset` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `class` tinyint(2) unsigned NOT NULL DEFAULT '0' COMMENT '语义倾向性： 0：中性、客观 1褒义 2、贬义',
  `title` varchar(255) NOT NULL DEFAULT '',
  `url` varchar(255) NOT NULL DEFAULT '',
  `content` longtext,
  `source` varchar(255) NOT NULL DEFAULT '' COMMENT '新闻来源',
  `time` datetime DEFAULT '0000-00-00 00:00:00' COMMENT '新闻发布时间',
  PRIMARY KEY (`id`)
